import React, { useEffect, useState } from 'react';
import "./SpeechToText.js"

const SpeechRecognitionComponent = () => {
  const [texts, setTexts] = useState([]);

  useEffect(() => {
    let recognition = null;
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;

    if (SpeechRecognition) {
      recognition = new SpeechRecognition();
      recognition.interimResults = true;

      recognition.addEventListener("result", (e) => {
        const transcript = Array.from(e.results)
          .map((result) => result[0])
          .map((result) => result.transcript)
          .join("");

        setTexts(prevTexts => [...prevTexts, transcript]);

        if (e.results[0].isFinal) {
          if (transcript.includes("how are you")) {
            setTexts(prevTexts => [...prevTexts, "I am fine"]);
          }
          if (
            transcript.includes("what's your name") ||
            transcript.includes("what is your name")
          ) {
            setTexts(prevTexts => [...prevTexts, "My Name is Pandora"]);
          }
          if (transcript.includes("open YouTube")) {
            setTexts(prevTexts => [...prevTexts, "opening youtube channel"]);
            window.open("https://www.youtube.com");
          }
        }
      });

      recognition.addEventListener("end", () => {
        recognition.start();
      });

      recognition.start();
    } else {
      console.log("Speech recognition not supported in this browser.");
    }

    return () => {
      if (recognition) {
        recognition.removeEventListener("result");
        recognition.removeEventListener("end");
        recognition.stop();
      }
    };
  }, []);

  return (
    <section>
      <h1>Speech Recognition</h1>
      <p>Available In Chrome Only</p>
      <div className="container">
        <div className="texts">
          {texts.map((text, index) => (
            <p key={index} className={text.includes("I am") ? "replay" : ""}>{text}</p>
          ))}
        </div>
      </div>
    </section>
  );
};

export default SpeechRecognitionComponent;
